executionPlan.queryExpressions:
define table groupDaySpendLimitDataTable(groupName string, consumerKey string, operatorId string,msisdn string, amount double,currentDateTime string,responseTime long) from ('datasource.name'='AXIATA_MIFE_DB', 'table.name'='day_spendlimitdata');

from SpendLimitData
select groupName,consumerKey,operatorId,msisdn,chargeAmount, false as reset,paymentType,currentDateTime,responseTime
insert into SpendLimitSummingStream;

from SpendLimitSummingStream#window.mife:cron('0 0 0 * * ?','groupName','operatorId','msisdn')
select groupName,consumerKey,operatorId,msisdn, mife:optsum(chargeAmount,reset,paymentType,groupName ) as totalAmount, reset,currentDateTime,responseTime
group by groupName,operatorId,msisdn
insert into totalStream;

from totalStream[reset==true]
delete groupDaySpendLimitDataTable
on totalStream.groupName == groupDaySpendLimitDataTable.groupName;

from totalStream[reset!=true ]
select groupName,consumerKey,operatorId,msisdn, totalAmount,currentDateTime,responseTime
insert into groupDaySpendLimitDataTable;
